INSERT INTO `cases` VALUES ('286', '某寺庙1KW离网太阳能发电系统', '', '<div style=\"text-align: center;\">\r\n	<img alt=\"离网太阳能发电系统\" src=\"/uploads/allimg/151031/1-151031134912Y2.jpg\" style=\"width: 550px; height: 742px;\" /><br />\r\n	<br />\r\n	<img alt=\"离网太阳能发电系统\" src=\"/uploads/allimg/151031/1-15103113493Q91.jpg\" style=\"width: 550px; height: 742px;\" /><br />\r\n	<br />\r\n	<img alt=\"离网太阳能发电系统\" src=\"/uploads/allimg/151031/1-151031135015295.jpg\" style=\"width: 550px; height: 742px;\" /></div>\r\n', '/uploads/allimg/151031/1-151031134912Y2.jpg', 'case', 'power');
INSERT INTO `cases` VALUES ('287', '攀枝花仁和区中坝乡独家村光伏提灌站项目建设', '此项目为24KW光伏提灌站系统，工程地点为攀枝花仁和区中坝乡独家村，据了解攀枝花地区日晒阳光充足，全市太阳能分布式发电系统集成能力达到50兆瓦以上。', '<div style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\"><img border=\"0\" src=\"/uploads/allimg/160105/1A64923c-0.jpg\" style=\"border:0px;\" /></span></div>\r\n<div style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\">太阳能电池板支架搭建</span></div>\r\n<div style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\"><img border=\"0\" src=\"/uploads/allimg/160105/1A64a319-1.jpg\" style=\"border:0px;\" /></span></div>\r\n<div style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\">太阳能电池组件安装</span></div>\r\n<div style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\"><img border=\"0\" src=\"/uploads/allimg/160105/1A64932I-2.jpg\" style=\"border:0px;\" /></span></div>\r\n<div style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\">100块太阳能电池板全部投入使用</span></div>\r\n<div style=\"text-align: center;\">\r\n	&nbsp;</div>\r\n<div>\r\n	<span style=\"font-size:14px;\">&nbsp; &nbsp;&nbsp; &nbsp; 此项目为24KW光伏提灌站系统，工程地点为攀枝花仁和区中坝乡独家村，据了解攀枝花地区日晒阳光充足，全市太阳能分布式发电系统集成能力达到50兆瓦以上。<br />\r\n	&nbsp; &nbsp; &nbsp;&nbsp;&nbsp;目前攀枝花重点推广太阳能组件运行在25年以上、20年光电转换效率衰减不超过20%、水泵稳定运行5年以上的太阳能提灌应用，到2017年建成30兆瓦太阳能提灌工程，实现灌溉林地3-5万亩。到2017年建成10兆瓦太阳能提灌工程，实现节水灌溉农地1-3万亩和解决部分缺水山区农村生活用水，建成100户无电山区农户太阳能发电户用系统。<br />\r\n	<br />\r\n	&nbsp;&nbsp; &nbsp; &nbsp;&nbsp;四川蜀旺科技有限公司承接的此项目于2015年12月31日顺利通过二次验收并正式投入使用。</span></div>\r\n', '/uploads/allimg/160105/1A64923c-0.jpg', 'case', 'power');
INSERT INTO `cases` VALUES ('289', '游仙区供电局太阳能发电并网示范性工程', '', '<div style=\"text-align: center;\">\r\n	<img alt=\"太阳能发电并网\" src=\"/uploads/allimg/160307/1-16030G53330314.jpg\" style=\"width: 600px; height: 450px;\" /><br />\r\n	<br />\r\n	<img alt=\"\" src=\"/uploads/allimg/160307/1-16030G53431157.jpg\" style=\"width: 600px; height: 450px;\" /><br />\r\n	<br />\r\n	<img alt=\"\" src=\"/uploads/allimg/160527/1-16052G51256332.jpg\" style=\"width: 600px; height: 450px;\" /><br />\r\n	<br />\r\n	<img alt=\"\" src=\"/uploads/allimg/160527/1-16052G51401R3.jpg\" style=\"width: 600px; height: 800px;\" /><br />\r\n	<br />\r\n	<img alt=\"\" src=\"/uploads/allimg/160527/1-16052G514321G.jpg\" style=\"width: 600px; height: 800px;\" /><br />\r\n	<br />\r\n	<img alt=\"\" src=\"/uploads/allimg/160527/1-16052G51452I5.jpg\" style=\"width: 600px; height: 450px;\" /></div>\r\n', '/uploads/allimg/160307/1-16030G53330314.jpg', 'case', 'power');
INSERT INTO `cases` VALUES ('290', '用马背驼出来的离网太阳能发电系统', '项目名称：江油市林业局护林小组离网太阳能发电系统 项目地点：江油市新春乡藏王寨，海拔 2246米 特写：不通公路，所有设备人背马拖，徒步3.5小时到达项目地 成果：结束护林人15年没有电的历史', '<br />\r\n<span style=\"font-size:14px;\">项目名称：江油市林业局护林小组离网太阳能发电系统<br />\r\n项目地点：江油市新春乡黄鹂垭护林站，海拔<span style=\"color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; line-height: 24px; text-indent: 28px;\">2246米</span><br />\r\n特写：不通公路，所有设备人背马拖，徒步3.5小时到达项目地<br />\r\n成果：结束护林人15年没有电的历史<br />\r\n项目掠影如下：<br />\r\n<img alt=\"\" src=\"/uploads/allimg/160307/1-16030G5515L06.jpg\" style=\"width: 680px; height: 510px;\" /></span><br />\r\n', '/uploads/allimg/160307/1-16030G5515L06.jpg', 'case', 'power');
INSERT INTO `cases` VALUES ('294', '某品牌门业集团5kw并网光伏发电系统', '此5kw光伏并网发电系统由20块多晶A级260w多晶太阳能电池板组成 太阳能电池板支架由客户自行焊接 太阳能电池板采用10串2并的接线方式 5kw光伏并网发电系统在多云天气下发电的实时数据', '<div style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\"><img alt=\"\" src=\"/uploads/allimg/160527/1-16052G45F5917.jpg\" style=\"width: 600px; height: 450px;\" /><br />\r\n	此5kw光伏并网发电系统由20块多晶A级260w多晶太阳能电池板组成<br />\r\n	<img alt=\"\" src=\"/uploads/allimg/160527/1-16052G45HO23.jpg\" style=\"width: 600px; height: 450px;\" /><br />\r\n	太阳能电池板支架由客户自行焊接<br />\r\n	<img alt=\"\" src=\"/uploads/allimg/160527/1-16052G45JbE.jpg\" style=\"width: 600px; height: 450px;\" /><br />\r\n	太阳能电池板采用10串2并的接线方式<br />\r\n	<img alt=\"\" src=\"/uploads/allimg/160527/1-16052G45Q03T.jpg\" style=\"width: 600px; height: 450px;\" /><br />\r\n	5kw光伏并网发电系统在多云天气下发电的实时数据</span></div>\r\n', '/uploads/allimg/160527/1-16052G45F5917.jpg', 'case', 'power');
