INSERT INTO `cases` VALUES ('346', '成都新都区某工厂25kw并离网储能光伏车棚', '工程地点：成都市新都区某工厂 装机容量：25KW 组件类型：蜀旺A级多晶硅265w 逆变器品牌：古瑞瓦特 储能方案：磷酸铁锂储能系统 施工方：四川蜀旺新能源股份有限公司 下面开始放毒 NO.1厚实的用料 NO.2优美而又牢固的焊缝，百年屹立不倒 NO.3辛勤又专业的施', '<p style=\"margin: 0px; padding: 0px; clear: both; color: rgb(0, 0, 0); font-family: &quot;Helvetica Neue&quot;, Helvetica, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, Arial, sans-serif; font-size: medium;\">\r\n	<span style=\"margin: 0px; padding: 0px; vertical-align: baseline; font-size: 14px;\">工程地点：成都市新都区某工厂</span></p>\r\n<p style=\"margin: 0px; padding: 0px; clear: both; color: rgb(0, 0, 0); font-family: &quot;Helvetica Neue&quot;, Helvetica, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, Arial, sans-serif; font-size: medium;\">\r\n	<span style=\"margin: 0px; padding: 0px; vertical-align: baseline; font-size: 14px;\">装机容量：25KW</span></p>\r\n<p style=\"margin: 0px; padding: 0px; clear: both; color: rgb(0, 0, 0); font-family: &quot;Helvetica Neue&quot;, Helvetica, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, Arial, sans-serif; font-size: medium;\">\r\n	<span style=\"margin: 0px; padding: 0px; vertical-align: baseline; font-size: 14px;\">组件类型：蜀旺A级多晶硅265w</span></p>\r\n<p style=\"margin: 0px; padding: 0px; clear: both; color: rgb(0, 0, 0); font-family: &quot;Helvetica Neue&quot;, Helvetica, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, Arial, sans-serif; font-size: medium;\">\r\n	<span style=\"margin: 0px; padding: 0px; vertical-align: baseline; font-size: 14px;\">逆变器品牌：古瑞瓦特</span></p>\r\n<p style=\"margin: 0px; padding: 0px; clear: both; color: rgb(0, 0, 0); font-family: &quot;Helvetica Neue&quot;, Helvetica, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, Arial, sans-serif; font-size: medium;\">\r\n	<span style=\"margin: 0px; padding: 0px; vertical-align: baseline; font-size: 14px;\">储能方案：磷酸铁锂储能系统</span></p>\r\n<p style=\"margin: 0px; padding: 0px; clear: both; color: rgb(0, 0, 0); font-family: &quot;Helvetica Neue&quot;, Helvetica, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, Arial, sans-serif; font-size: medium;\">\r\n	<span style=\"margin: 0px; padding: 0px; vertical-align: baseline; font-size: 14px;\">施工方：四川蜀旺新能源股份有限公司</span></p>\r\n<p style=\"margin: 0px; padding: 0px; clear: both; color: rgb(0, 0, 0); font-family: &quot;Helvetica Neue&quot;, Helvetica, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, Arial, sans-serif; font-size: medium;\">\r\n	<span style=\"margin: 0px; padding: 0px; color: rgb(255, 76, 65); vertical-align: baseline; font-size: 14px;\">下面开始放毒</span><span style=\"margin: 0px; padding: 0px; vertical-align: baseline; font-size: 14px;\"><img _src=\"https://res.wx.qq.com/mpres/htmledition/images/icon/common/emotion_panel/smiley/smiley_13.png\" data-ratio=\"1\" data-w=\"20\" src=\"https://res.wx.qq.com/mpres/htmledition/images/icon/common/emotion_panel/smiley/smiley_13.png\" style=\"margin: 0px; padding: 0px; max-width: 100%; height: auto !important; display: inline-block; width: 20px; vertical-align: text-bottom;\" /><img _src=\"https://res.wx.qq.com/mpres/htmledition/images/icon/common/emotion_panel/smiley/smiley_13.png\" data-ratio=\"1\" data-w=\"20\" src=\"https://res.wx.qq.com/mpres/htmledition/images/icon/common/emotion_panel/smiley/smiley_13.png\" style=\"margin: 0px; padding: 0px; max-width: 100%; height: auto !important; display: inline-block; width: 20px; vertical-align: text-bottom;\" /></span></p>\r\n<p style=\"margin: 0px; padding: 0px; clear: both; color: rgb(0, 0, 0); font-family: &quot;Helvetica Neue&quot;, Helvetica, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, Arial, sans-serif; font-size: medium;\">\r\n	<span style=\"margin: 0px; padding: 0px; vertical-align: baseline; font-size: 14px;\"><strong style=\"margin: 0px; padding: 0px; color: rgb(0, 209, 0);\">NO.1厚实的用料</strong></span></p>\r\n<p style=\"margin: 0px; padding: 0px; clear: both; color: rgb(0, 0, 0); font-family: &quot;Helvetica Neue&