INSERT INTO `spl_content` VALUES ('2d4ff786ae064f9d8f77cf2968469e90', 'product', '<p>start</p>\n\n<p>&nbsp;</p>\n\n<p><span style=\"font-family:Georgia,serif\"><em><strong>这是一个</strong></em></span></p>\n\n<p>&nbsp;</p>\n\n<h2><span style=\"font-family:Georgia,serif\"><em><strong>what ？</strong></em></span></h2>\n', '', '', 'luguo', '1', null, null);
INSERT INTO `spl_content` VALUES ('4979f5c4af7848cbb8faf423a6016eb3', 'recruit', '\n     <div>\n        <div class=\"container\">\n            <section class=\"content\">\n                <div class=\"display\">\n                    <!--=----------------------------------------------------------------------------------=-->\n\n                    <div class=\"text-center\">\n                        <img src=\"/images/contact/join-us.png\">\n                    </div>\n\n                    <div class=\"row\" style=\"padding-top: 5vw\">\n                        <div class=\"col-sm-6 contact-p-div\">\n                            <img src=\"/images/contact/WELCOMETOSHUWANG.png\" style=\"padding-bottom: 3vw\">\n                            <p>我们在涪城区南湖电子信息工业园，蜀旺新能源的团队积极的将太阳能</p>\n                            <p>智慧解决方案带到世界各地，同时也需要应对来自世界各的能源挑战。</p>\n                            <p>蜀旺新能源将提高全世界人民的生活水平视为一项神圣使命，</p>\n                            <p>蜀旺致力于自身员工的健康，安全和发展。</p>\n                            <p>现在你将有机会与富有创新精神的蜀旺团队共同合作，</p>\n                            <p>在太阳能行业的快速发展中得到职业的发展与自我的成长。</p>\n                            <p>我们有一群吃不胖的人！我们是充满激情和活力的团队！</p>\n                            <p>公司不大不小50个人，</p>\n                            <p>一群开放的领导满足你的一切愿望，</p>\n                            <p>虽然要求严格，</p>\n                            <p>但是会让你快速成长成一名拥有职业素养和专业素养的人！</p>\n                            <p>欢迎来这里！！！</p>\n                        </div>\n                        <div class=\"col-sm-6\"><img src=\"/images/contact/contact3.png\"></div>\n                    </div>\n\n                    <!--=-------------------------------------------------------------------------------------=-->\n                </div>\n            </section>\n        </div>\n    </div>\n', '', '', 'Qays', '1', null, null);
INSERT INTO `spl_content` VALUES ('4a33a8095e8544728cec523152df8aec', 'industrial-base', '\n    <div>\n        <div class=\"container\">\n            <section class=\"content\">\n                <div class=\"display\">\n                    <!--=----------------------------------------------------------------------------------=-->\n\n                    <div class=\"a-h-div\">\n                        <h1>生产基地</h1>\n                    </div>\n\n                    <div class=\"a-p-div1\">\n                        <p>只有为客户创造价值才能为自己创造价值，以客户需求为中心，通过专业、贴心、高效的服务指导客户掌握使用、保养、施工、维修等方法和技巧，为客户提供保障、解决难题创造最大价值</p>\n                        <p>我们聚集了不同领域的专业人才，具备雄厚的技术资源，以及与国外多家科研机构和组织合作创建的光伏组件高新技术产品研制团队，保证了组件产品不断更新。我们可以提供多款组件和数种方案，为您设计多种可能的组合使您订购的光伏组件与您的需求完全契合</p>\n                    </div>\n\n                    <div class=\"a-img-div1\">\n                        <div class=\"col-lg-3 col-md-6 col-sm-12 a-img-div1-sub\">\n                            <img class=\"credit-img\" src=\"/images/about/base5.png\">\n                        </div>\n                        <div class=\"col-lg-3 col-md-6 col-sm-12 a-img-div1-sub\">\n                            <img class=\"credit-img\" src=\"/images/about/base6.png\">\n                        </div>\n                        <div class=\"col-lg-3 col-md-6 col-sm-12 a-img-div1-sub\">\n                            <img class=\"credit-img\" src=\"/images/about/base7.png\">\n                        </div>\n                        <div class=\"col-lg-3 col-md-6 col-sm-12 a-img-div1-sub\">\n                            <img class=\"credit-img\" src=\"/images/about/base8.png\">\n                        </div>\n                    </div>\n\n                    <!--=-------------------------------------------------------------------------------------=-->\n                </div>\n\n            </section>\n        </div>\n    </div>\n', '', '', 'Qays', '1', null, null);
INSERT INTO `spl_content` VALUES ('5286698b48f04bcc8311d9e7e9e239c0', 'contact-m', '\n      <div>\n        <div class=\"container\">\n            <section class=\"content\">\n                <div class=\"display\">\n                    <!--=----------------------------------------------------------------------------------=-->\n\n                    <div class=\"text-center\" style=\"padding: 2vw\">\n                        <img src=\"/images/contact/contact2.png\">\n                    </div>\n\n                    <div class=\"row\">\n                        <div class=\"col-sm-4\">\n                            <h3>四川蜀旺新能源股份有限公司</h3>\n                            <div class=\"contact-p\">\n                                <p><b>客服电话：400-682667</b></p>\n                                <p>地址：绵阳市经济技术开发区南湖电子信息工业园</p>\n                                <p>电话:0816-2302374</p>\n                                <p>邮箱：sales@swpv.net</p>\n                            </div>\n                        </div>\n                        <div class=\"col-sm-8\">\n                            <iframe style=\"min-height: 400px\" class=\"custom-i\" src=\"http://www.swkj.net.cn/baiduMap.html\" frameborder=\"0\" scrolling=\"no\"></iframe>\n                        </div>\n                    </div>\n\n                    <div class=\"text-center\" style=\"padding: 2vw\">\n                        <img src=\"/images/contact/contact.png\">\n                    </div>\n\n                    <form role=\"form\" class=\"text-center\">\n\n                        <div class=\"form-group col-sm-6\">\n                            <div class=\"input-group input-group-lg\">\n                                <div class=\"input-group-addon\">\n                                    <i class=\"glyphicon glyphicon-user\"></i>\n                                </div>\n                                <input id=\"name\" type=\"text\" placeholder=\"姓名\" class=\"form-control\">\n                                <div class=\"input-group-addon bg-blue hidden-xs\">\n                                    <i class=\"glyphicon glyphicon-music\"></i>\n                                </div>\n                            </div>\n\n                        </div>\n\n                        <div class=\"form-group col-sm-6 has-error\">\n                            <div class=\"input-group input-group-lg\">\n                                <div class=\"input-group-addon\">\n                                    <i class=\"glyphicon glyphicon-envelope\"></i>\n                                </div>\n                                <input id=\"email\" type=\"email\" placeholder=\"邮箱\" class=\"form-control\">\n                                <div class=\"input-group-addon bg-red hidden-xs\">\n                                    <i class=\"glyphicon glyphicon-send\"></i>\n                                </div>\n                            </div>\n                        </div>\n\n                        <div class=\"form-group col-sm-6 has-success\">\n                            <div class=\"input-group input-group-lg\">\n                                <div class=\"input-group-addon\">\n                                    <i class=\"glyphicon glyphicon-phone-alt\"></i>\n                                </div>\n                                <input id=\"phone\" type=\"tel\" placeholder=\"电话\" class=\"form-control\">\n                                <div class=\"input-group-addon bg-green hidden-xs\">\n                                    <i class=\"glyphicon glyphicon-comment\"></i>\n                                </div>\n                            </div>\n                        </div>\n\n                        <div class=\"form-group col-sm-6 has-warning\">\n                            <div class=\"input-group input-group-lg\">\n                                <div class=\"input-group-addon\">\n                                    <i class=\"glyphicon glyphicon-home\"></i>\n                                </div>\n                                <input id=\"address\" type=\"text\" placeholder=\"住址\" class=\"form-control\">\n                                <div class=\"input-group-addon bg-orange hidden-xs\">\n                                    <i class=\"glyphicon glyphicon-tree-deciduous\"></i>\n                                </div>\n                            </div>\n                        </div>\n\n                        <div class=\"form-group col-sm-12\">\n                            <textarea class=\"form-control\" id=\"msg\" rows=\"5\" placeholder=\"留言信息\"></textarea>\n                        </div>\n\n                        <button type=\"button\" id=\"suggests-submit\" class=\"btn btn-info btn-lg btn-flat\">提交信息</button>\n                    </form>\n\n                    <!--=-------------------------------------------------------------------------------------=-->\n                </div>\n            </section>\n        </div>\n    </div>\n\n    <script>\n        function suggests(name, phone, email, address, msg) {\n            $.ajax({\n                url:\'/api/sendMail\',\n                type:\'GET\',\n                data:{name:name,phone:phone,email:email,address:address, msg:msg},\n                success:function (d, s) {\n                    if(d == \'success\')\n                        alert(\'发送成功\');\n                    else if(d == \'frequent\')\n                        alert(\'频繁请求\');\n                    else\n                        alert(\'未知错误，你可以直接拨打电话：xxxx\');\n                },\n                error:function (d, s) {\n                    alert(\'出现未知错误，你可以直接拨打电话：xxxx\');\n                }\n            });\n        }\n\n        $(\'document\').ready(function () {\n            $(\'#suggests-submit\').click(function () {\n                if(eleIsNull(\'name\'))\n                    alert(\'未填写姓名\');\n                else if(eleIsNull(\'phone\'))\n                    alert(\'未填写电话\')\n                else{\n                    var name = $(\'#name\').val();\n                    var phone = $(\'#phone\').val();\n                    var email = $(\'#email\').val();\n                    var address = $(\'#address\').val();\n                    var msg = $(\'#msg\').val();\n                    suggests(name, phone, email, address, msg);\n                }\n\n            });\n        });\n    </script>\n', '', '', 'Qays', '1', null, null);
INSERT INTO `spl_content` VALUES ('99c2c79bf29648b8a5a4444935af4b9b', 'advantage', '\n    <div>\n        <div class=\"container\">\n            <section class=\"content\">\n                <div class=\"display\">\n                    <!--=----------------------------------------------------------------------------------=-->\n\n                    <div style=\"text-align: center\">\n                        <div>\n                            <h1 class=\"animated delay600 wow fadeInDown\" style=\"color: #7c75d8b3;padding-bottom: 1.5vw\">独到品牌优势，优质品质不止于此</h1>\n                            <p class=\"animated delay300 wow fadeInUp\" style=\"font-size: 1.3em;font-weight: 600;color: #090a01ab;padding-bottom: 2vw\">大力发展新能源，为资源节约型和环境友好型社会做贡献，为企业创效益，为员工某福祉，为子孙后代的蓝天绿地不懈努力</p>\n                        </div>\n\n                        <img class=\"animated delay900 wow lightSpeedIn\" src=\"/images/about/pictureOnScreen.png\">\n\n                        <div class=\"row\">\n                            <div class=\"custom-col animated delay1500 wow fadeInDown\">\n                                <img src=\"/images/about/advantage-zb.png\"/>\n                                <h1>厂家直销</h1>\n                                <p>我们是太阳能电池生产厂家</p>\n                                <p>采用厂家直销方式,直接面对</p>\n                                <p>终端客户,节省一大笔</p>\n                                <p>终端费用</p>\n                            </div>\n                            <div class=\"custom-col animated delay900 wow fadeInDown\">\n                                <img src=\"/images/about/advantage-ks.png\"/>\n                                <h1>15分钟内相应</h1>\n                                <p>快速相应你的售前咨询</p>\n                                <p>给你专业赖心解答</p>\n                            </div>\n                            <div class=\"custom-col animated delay600 wow fadeInDown\">\n                                <img src=\"/images/about/advantage-rt.png\"/>\n                                <h1>价格优势</h1>\n                                <p>公司在优质的光伏、光热系统</p>\n                                <p>研发生产中，建立了快捷</p>\n                                <p>高效的系统化销售渠</p>\n                                <p>道，厂家直销无中</p>\n                                <p>间差价</p>\n                            </div>\n                            <div class=\"custom-col animated delay900 wow fadeInDown\">\n                                <img src=\"/images/about/advantage-yx.png\"/>\n                                <h1>售后响应</h1>\n                                <p>7*24小时全时段服务，提供</p>\n                                <p>时效极致体验</p>\n                            </div>\n                            <div class=\"custom-col animated delay1500 wow fadeInDown\">\n                                <img src=\"/images/about/advantage-ks.png\"/>\n                                <h1>产品专注</h1>\n                                <p>线上定向曝光 + 线下精细耕作</p>\n                                <p>的营销模式，产品曝光度</p>\n                                <p>与日俱增</p>\n                            </div>\n                        </div>\n\n                    </div>\n\n                    <!--=-------------------------------------------------------------------------------------=-->\n                </div>\n            </section>\n        </div>\n    </div>\n', '', '', 'Qays', '1', null, null);
INSERT INTO `spl_content` VALUES ('9c2ff036790a4f0db5b3bef4fac89882', 'supports', '\n      <div>\n        <div class=\"product-m\">\n            <div>\n                <h1>售后服务入口</h1>\n                <p>蜀旺新能源专注于为全世界的客户提供服务，建立起一个多层次，多渠道的快速高效客服服务团队</p>\n                <button type=\"button\" class=\"btn bg-red btn-flat btn-lg\">了解更多</button>\n            </div>\n        </div>\n\n        <div class=\"product-m product-b\" style=\"background-image: url(\'/images/contact/quality.png\'); display: flex;align-items: center\">\n            <div class=\"container\">\n                <div class=\"row\">\n                    <div class=\"col-sm-4 product-p\" style=\"color: white;\">\n                        <h1>客户质量保障</h1>\n                        <p>蜀旺新能源专业客户质量部门负责处理客户的反馈和要求，确保客户满意度。质量检测部门还负责工厂审计和客服的检查，保证所有客户参观、审计及产品检查检查顺利进行</p>\n                    </div>\n                </div>\n            </div>\n        </div>\n\n        <div class=\"product-m\">\n            <div class=\"container\">\n                <div class=\"row\">\n                    <div class=\"col-sm-12\">\n                        <h1>售前支持</h1>\n                        <p>我们售前服务团队提供技术信息和销售相关的资讯服务，如下</p>\n                    </div>\n                </div>\n\n                <div class=\"row\">\n                    <div class=\"col-sm-4\">\n                        <a href=\"/product\">产品资讯</a>\n                        <p>产品规格，证书等</p>\n                    </div>\n                    <div class=\"col-sm-4\">\n                        <a href=\"/contact\">销售资讯</a>\n                        <p>购买渠道，有效性等</p>\n                    </div>\n                    <div class=\"col-sm-4\">\n                        <a href=\"/contact\">通用资讯</a>\n                        <p>其他问题</p>\n                    </div>\n\n                </div>\n            </div>\n        </div>\n\n        <div class=\"product-m\">\n            <div class=\"container\">\n                <div class=\"row\">\n                    <div class=\"col-sm-12\">\n                        <h1>售后支持</h1>\n                        <p>关于质保和退换货的相关问题，请联系我们售后服务代表</p>\n                        <a href=\"/contact\">组件索赔</a>\n                    </div>\n                </div>\n            </div>\n        </div>\n    </div>\n\n\n', '', '', 'Qays', '1', null, null);
INSERT INTO `spl_content` VALUES ('a64c7d31c9ea4dcd92ea9e8310bd9d16', 'Accreditation', '\n    <div>\n        <div class=\"container\">\n            <section class=\"content\">\n                <div class=\"display\">\n                    <!--=----------------------------------------------------------------------------------=-->\n\n                    <div class=\"a-h-div\">\n                        <h1>认证资质</h1>\n                    </div>\n\n                    <div class=\"a-img-div1\">\n                        <div class=\"col-lg-3 col-md-6 col-sm-12 a-img-div1-sub\">\n                            <img class=\"credit-img animated delay300 wow fadeInUp\" src=\"/images/about/7.jpg\">\n                        </div>\n                        <div class=\"col-lg-3 col-md-6 col-sm-12 a-img-div1-sub\">\n                            <img class=\"credit-img animated delay600 wow fadeInDown\" src=\"/images/about/7.jpg\">\n                        </div>\n                        <div class=\"col-lg-3 col-md-6 col-sm-12 a-img-div1-sub\">\n                            <img class=\"credit-img animated delay1200 wow fadeInUp\" src=\"/images/about/7.jpg\">\n                        </div>\n                        <div class=\"col-lg-3 col-md-6 col-sm-12 a-img-div1-sub\">\n                            <img class=\"credit-img animated delay1500 wow fadeInDown\" src=\"/images/about/7.jpg\">\n                        </div>\n\n                    </div>\n\n                    <div class=\"a-img-div1\">\n                        <div class=\"col-lg-3 col-md-6 col-sm-12 a-img-div1-sub\">\n                            <img class=\"credit-img animated delay1500 wow lightSpeedIn\" src=\"/images/about/zizhi2.jpg\">\n                        </div>\n                        <div class=\"col-lg-3 col-md-6 col-sm-12 a-img-div1-sub\">\n                            <img class=\"credit-img animated delay900 wow fadeInRight\" src=\"/images/about/zizhi2.jpg\">\n                        </div>\n                        <div class=\"col-lg-3 col-md-6 col-sm-12 a-img-div1-sub\">\n                            <img class=\"credit-img animated delay600 wow fadeInRight\" src=\"/images/about/zizhi2.jpg\">\n                        </div>\n                        <div class=\"col-lg-3 col-md-6 col-sm-12 a-img-div1-sub\">\n                            <img class=\"credit-img animated delay300 wow fadeInRight\" src=\"/images/about/zizhi2.jpg\">\n                        </div>\n                    </div>\n\n                    <!--=-------------------------------------------------------------------------------------=-->\n                </div>\n            </section>\n        </div>\n    </div>', '', '', 'Qays', '1', null, null);
INSERT INTO `spl_content` VALUES ('a6fd3930769f403c81ae18715cee6a3f', 'corporate-spirit', '\n    \n    <div>\n        <div class=\"container\">\n            <section class=\"content\">\n                <div class=\"display\" style=\"padding-bottom: 2vw\">\n                    <!--=----------------------------------------------------------------------------------=-->\n\n                    <div class=\"a-h-div animated delay600 wow fadeInDown\">\n                        <h1>公司理念</h1>\n                    </div>\n\n                    <div class=\"col-sm-6\">\n                        <img class=\"animated delay600 wow rollIn\" src=\"/images/about/spirit-banner.png\">\n                    </div>\n\n                    <div class=\"col-sm-6 animated delay600 wow bounceInRight\" style=\"padding: 0 3vw\">\n                        <h3>服务理念</h3>\n                        <p>让蜀旺成为国内技术一流太阳能电池厂</p>\n                        <h3>品牌理念</h3>\n                        <p>让中国人都能用上便宜、干净的太阳能</p>\n                        <h3>人才理念</h3>\n                        <p>志存高远，与阳光同行</p>\n                        <h3>创新理念</h3>\n                        <p>理解、勤奋、严谨创新</p>\n                    </div>\n\n                    <!--=-------------------------------------------------------------------------------------=-->\n                </div>\n            </section>\n        </div>\n    </div>\n\n', '', '', 'Qays', '1', null, null);
INSERT INTO `spl_content` VALUES ('b1c3391ec247452bbf9d9ce2f94cba72', 'suggests', '\n    <div class=\"container\">\n        <section class=\"content\">\n            <div class=\"display\">\n                <!--=----------------------------------------------------------------------------------=-->\n\n                <div class=\"text-center\" style=\"padding: 2vw\">\n                    <img src=\"/images/contact/contact.png\">\n                </div>\n\n                <form role=\"form\" class=\"text-center\">\n\n                    <div class=\"form-group col-sm-6\">\n                        <div class=\"input-group input-group-lg\">\n                            <div class=\"input-group-addon\">\n                                <i class=\"glyphicon glyphicon-user\"></i>\n                            </div>\n                            <input id=\"name\" type=\"text\" placeholder=\"姓名\" class=\"form-control\">\n                            <div class=\"input-group-addon bg-blue hidden-xs\">\n                                <i class=\"glyphicon glyphicon-music\"></i>\n                            </div>\n                        </div>\n\n                    </div>\n\n                    <div class=\"form-group col-sm-6 has-error\">\n                        <div class=\"input-group input-group-lg\">\n                            <div class=\"input-group-addon\">\n                                <i class=\"glyphicon glyphicon-envelope\"></i>\n                            </div>\n                            <input id=\"email\" type=\"email\" placeholder=\"邮箱\" class=\"form-control\">\n                            <div class=\"input-group-addon bg-red hidden-xs\">\n                                <i class=\"glyphicon glyphicon-send\"></i>\n                            </div>\n                        </div>\n                    </div>\n\n                    <div class=\"form-group col-sm-6 has-success\">\n                        <div class=\"input-group input-group-lg\">\n                            <div class=\"input-group-addon\">\n                                <i class=\"glyphicon glyphicon-phone-alt\"></i>\n                            </div>\n                            <input id=\"phone\" type=\"tel\" placeholder=\"电话\" class=\"form-control\">\n                            <div class=\"input-group-addon bg-green hidden-xs\">\n                                <i class=\"glyphicon glyphicon-comment\"></i>\n                            </div>\n                        </div>\n                    </div>\n\n                    <div class=\"form-group col-sm-6 has-warning\">\n                        <div class=\"input-group input-group-lg\">\n                            <div class=\"input-group-addon\">\n                                <i class=\"glyphicon glyphicon-home\"></i>\n                            </div>\n                            <input id=\"address\" type=\"text\" placeholder=\"住址\" class=\"form-control\">\n                            <div class=\"input-group-addon bg-orange hidden-xs\">\n                                <i class=\"glyphicon glyphicon-tree-deciduous\"></i>\n                            </div>\n                        </div>\n                    </div>\n\n                    <div class=\"form-group col-sm-12\">\n                        <textarea class=\"form-control\" id=\"msg\" rows=\"5\" placeholder=\"留言信息\"></textarea>\n                    </div>\n\n                    <button type=\"button\" id=\"suggests-submit\" class=\"btn btn-info btn-lg btn-flat\">提交信息</button>\n                </form>\n\n                <!--=-------------------------------------------------------------------------------------=-->\n            </div>\n        </section>\n    </div>\n    <script>\n        function suggests(name, phone, email, address, msg) {\n            $.ajax({\n                url:\'/api/sendMail\',\n                type:\'GET\',\n                data:{name:name,phone:phone,email:email,address:address, msg:msg},\n                success:function (d, s) {\n                    if(d == \'success\')\n                        alert(\'发送成功\');\n                    else if(d == \'frequent\')\n                        alert(\'频繁请求\');\n                    else\n                        alert(\'未知错误，你可以直接拨打电话：xxxx\');\n                },\n                error:function (d, s) {\n                    alert(\'出现未知错误，你可以直接拨打电话：xxxx\');\n                }\n            });\n        }\n\n        $(\'document\').ready(function () {\n            $(\'#suggests-submit\').click(function () {\n                if(eleIsNull(\'name\'))\n                    alert(\'未填写姓名\');\n                else if(eleIsNull(\'phone\'))\n                    alert(\'未填写电话\')\n                else{\n                    var name = $(\'#name\').val();\n                    var phone = $(\'#phone\').val();\n                    var email = $(\'#email\').val();\n                    var address = $(\'#address\').val();\n                    var msg = $(\'#msg\').val();\n                    suggests(name, phone, email, address, msg);\n                }\n\n            });\n        });\n    </script>\n', '', '', 'Qays', '1', null, null);
INSERT INTO `spl_content` VALUES ('d1918d7252e24af297f820a47b236e8f', 'development-history', '\n', '/plugins/timeline/index.html', '', 'Qays', '1', null, null);
INSERT INTO `spl_content` VALUES ('eaa0ddf92d384c20b0c1ce1953dfa636', 'overview', '\n    \n    <div>\n        <div class=\"container\">\n            <section class=\"content\">\n                <div class=\"display\">\n                    <!--=----------------------------------------------------------------------------------=-->\n\n                    <div class=\"a-h-div animated delay600 wow fadeInDown animated\" style=\"visibility: visible; animation-name: fadeInDown;\">\n                        <h1>蜀旺新能源</h1>\n                        <p style=\"color: grey;font-size: 1.2em;font-variant: small-caps\">Shu Wang new energy</p>\n                    </div>\n\n                    <div class=\"a-p-div\">\n                        <p class=\"animated delay300 wow fadeInDown animated\" style=\"visibility: visible; animation-name: fadeInDown;\">\n                            四川蜀旺新能源股份有限公司股票代码：838456 创建于2006年，地址位于享誉中外的“西部硅谷”、“中国科技城”──四川绵阳国家级经济技术开发区，是一家专业从事新能源研究开发及产业化的高新技术企业。\n                        </p>\n                        <p class=\"animated delay600 wow fadeInDown animated\" style=\"visibility: visible; animation-name: fadeInDown;\">\n                            产业涉及太阳能光热利用、太能光伏发电系统及分布式太阳能发电站建设与维护太阳能路灯及LED照明、储能系统研究与开发等多个领域。\n                        </p>\n                        <p class=\"animated delay900 wow fadeInDown animated\" style=\"visibility: visible; animation-name: fadeInDown;\">\n                            近年来，实现了在业内的多企业、多产业联合，与多家知名企业和科研院所联合，强大了技术研发能力，完善了生产加工和检测的工艺流程，形成了集研发、生产、销售、服务一条龙的专业化服务型制造企业，是目前西部地区最具规模的新能源研发生产销售基地之一。\n                        </p>\n                    </div>\n\n                    <div class=\"a-img-div\">\n                        <div class=\"col-sm-4\">\n                            <img class=\"credit-img animated delay900 wow fadeInUp animated\" src=\"/images/about/overview1.png\" style=\"visibility: visible; animation-name: fadeInUp;\">\n                        </div>\n                        <div class=\"col-sm-4\">\n                            <img class=\"credit-img animated delay300 wow fadeInUp animated\" src=\"/images/about/overview2.png\" style=\"visibility: visible; animation-name: fadeInUp;\">\n                        </div>\n                        <div class=\"col-sm-4\">\n                            <img class=\"credit-img animated delay900 wow fadeInUp animated\" src=\"/images/about/overview3.png\" style=\"visibility: visible; animation-name: fadeInUp;\">\n                        </div>\n                    </div>\n\n                    <!--=-------------------------------------------------------------------------------------=-->\n                </div>\n            </section>\n        </div>\n    </div>\n\n\n', '', '', 'qays', '1', null, null);
